let redux = require('redux');
let isAuthenticate = '';
let username  = '';
let role  = '';
let token = ''
let selectType = null;
let user =localStorage.getItem('userStory');
if(user!=null){
    isAuthenticate = JSON.parse(user).isAuthenticate ? JSON.parse(user).isAuthenticate : '';
    username  = JSON.parse(user).isAuthenticate ? JSON.parse(user).username : '';
    role  = JSON.parse(user).isAuthenticate ? JSON.parse(user).role : '';
    token = JSON.parse(user).isAuthenticate ? JSON.parse(user).token : '';
    //selectType = JSON.parse(user).isAuthenticate ? JSON.parse(user).selectType : null;
}
let InitialState = { 
  
    isAuthenticate ,
    username,
    role,
    selectType
  
}

const allReducer = (state=InitialState,action)=>{
    switch(action.type){
        case  "IS_AUTHENTICATE" :
            localStorage.setItem('userStory',JSON.stringify({isAuthenticate:true,username :action.username,role:action.role,token:action.token}))
            return {isAuthenticate:true,username :action.username,role:action.role};
        case "IS_LOGOUT" :
            localStorage.setItem('userStory',JSON.stringify({isAuthenticate : false,username : '',role:'',token : ''}))    
            return {isAuthenticate : false,username : '',role:'',token : ''};
        case "SELECT_TYPE":
            return {selectType:action.typeSelect}
        default : 
            return {...state};

    }
}

let store = redux.createStore(allReducer,window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__());
store.subscribe(function(){
    console.log(JSON.stringify(store.getState().selectType));
})
export default store;
