import React, { Component } from 'react'
import { connect } from 'react-redux'

class Navigation extends Component {
  
    render() {
        return (
          <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <a className="navbar-brand" onClick={()=>this.props.selectType(null)}>HOME</a>
          <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon" />
          </button>
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav mr-auto">
              <li className="nav-item active">
                <a className="nav-link" href="# "><span className="sr-only">(current)</span></a>
              </li><li className="nav-item dropdown">
                <a className="nav-link dropdown-toggle" href="# " id="navbarDropdown" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                  Thể loại
                </a>
                <div className="dropdown-menu" aria-labelledby="navbarDropdown">
                  <a className="dropdown-item"  onClick={()=>this.props.selectType(1)}>Kinh dị</a>
                  <a className="dropdown-item" onClick={()=>this.props.selectType(2)}>Ngôn tình</a>
                  <a className="dropdown-item" onClick={()=>this.props.selectType(3)}>Viễn tưởng</a>
                </div>
              </li>
              <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon" />
              </button>
             
              <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon" />
              </button>
            </ul>
            <form className="form-inline my-2 my-lg-0">
            <div class="col-sm-8">
              <input className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" />
              </div>
              <div class="col-sm-4">
              <button className="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
              {
                this.props.isAuthenticate 
                  ? <button className="btn btn-outline-primary my-2 my-sm-0" type="submit">Logout</button> 
                  : <button 
                      className="btn btn-outline-primary my-2 my-sm-0" 
                      type="submit"
                      onClick = { ()=> this.props.selectType('login')}
                    >Login</button>  
              }
              </div>
            </form>
          </div>
        </nav>
        )
    }
}

const mapDispatchToProps = (dispatch,props) =>{
  return  {
    selectType : (typeSelect)=>{
      dispatch({
          type : "SELECT_TYPE",
          typeSelect
      })
    }
  }
}
const mapStateToProps = state =>{
  return {
      isAuthenticate : state.isAuthenticate
  }
}


export default  connect(mapStateToProps,mapDispatchToProps)(Navigation);
